import React, { useState } from "react";
import ReactMapGL from "react-map-gl";
import { List } from "antd";
import "../style/help.css";

function Help() {
  const [viewport, setViewport] = useState({
    width: 1130,
    height: 800,
    latitude: 45.216,
    longitude: -63.1978,
    zoom: 7.57,
  });
  const data = [
    {
      title: "Support for individuals and families",
      context:
        "",
    },
    {
      title:
        "Provincial parks in Nova Scotia reopen next week with COVID-19 guidelines",
      context:
        "Staff were busy posting new signs and painting physical-distancing markers around the amenities at at Dollar Lake Provincial Park on Friday ahead of the park’s reopening on June 15. In the wake of the COVID-19 pandemic, Nova Scotians will still be able to enjoy a camping season, but will need to follow a few health and safety guidelines. READ MORE: Nova Scotia extends state of emergency, reports no new coronavirus cases Friday",
    },
  ];
  return (
    <div className="map-box">
      <h1>COVID-19 help near you</h1>
      <p>
        This map shows some verified places and organisations where you can
        reach out for help, including financial, food and shelters, mental
        health and surports for children, seniors and people with disabilities.
      </p>
      <ReactMapGL
        {...viewport}
        mapboxApiAccessToken={
          "pk.eyJ1IjoiYW5xaTI3IiwiYSI6ImNrYmY3OW9kajBsaXYycXBjYjlkeHV2ZWQifQ.TRv69mW_zy814wzhMrwTFw"
        }
        mapStyle="mapbox://styles/anqi27/ckbf8p03e23a11htdg6evsdbx"
        onViewportChange={(nextViewport) => setViewport(nextViewport)}
      />
      <List
        header={
          <div>
            How we’re supporting individuals, families, seniors and businesses
            who are facing hardship because of the COVID-19 pandemic.
          </div>
        }
        itemLayout="vertical"
        dataSource={data}
        renderItem={(item) => (
          <List.Item>
            <div className="list-title">{item.title}</div>
            <div className="list-context">{item.context}</div>
          </List.Item>
        )}
      />
    </div>
  );
}

export default Help;
